import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { Store, Mail, Key, Eye, EyeOff, Sparkles, Zap, Shield, ArrowRight, CheckCircle, AlertCircle, Github, Chrome } from 'lucide-react';
import toast from 'react-hot-toast';

const Login = () => {
  const [email, setEmail] = useState('');
  const [tenantId, setTenantId] = useState('dev-tenant-001');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [emailValid, setEmailValid] = useState(null);
  const [tenantValid, setTenantValid] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const { login, user } = useAuth();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  useEffect(() => {
    // Check if user is already logged in
    if (user) {
      navigate('/dashboard');
    }

    // Check for tenant parameter from OAuth callback
    const tenant = searchParams.get('tenant');
    if (tenant) {
      setTenantId(tenant);
    }
  }, [user, navigate, searchParams]);

  // Mouse tracking for dynamic effects
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Email validation
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  // Tenant ID validation
  const validateTenantId = (tenantId) => {
    return tenantId && tenantId.length >= 3;
  };

  // Handle input changes with validation
  const handleEmailChange = (e) => {
    const value = e.target.value;
    setEmail(value);
    if (value) {
      setEmailValid(validateEmail(value));
    } else {
      setEmailValid(null);
    }
  };

  const handleTenantChange = (e) => {
    const value = e.target.value;
    setTenantId(value);
    if (value) {
      setTenantValid(validateTenantId(value));
    } else {
      setTenantValid(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !tenantId) {
      toast.error('Please fill in all fields');
      return;
    }

    setLoading(true);
    const result = await login(email, tenantId);
    setLoading(false);

    if (result.success) {
      navigate('/dashboard');
    }
  };

  const handleShopifyInstall = () => {
    const shop = prompt('Enter your Shopify store domain (e.g., mystore.myshopify.com):');
    if (shop) {
      window.location.href = `/auth/install?shop=${shop}`;
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50 py-12 px-4 sm:px-6 lg:px-8 relative overflow-hidden">
      {/* Enhanced Background with Dynamic Effects */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Animated gradient orbs */}
        <div 
          className="absolute w-96 h-96 bg-gradient-to-r from-blue-400 to-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"
          style={{
            top: `${mousePosition.y * 0.02}px`,
            left: `${mousePosition.x * 0.02}px`,
            transform: 'translate(-50%, -50%)'
          }}
        ></div>
        <div 
          className="absolute w-80 h-80 bg-gradient-to-r from-pink-400 to-red-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"
          style={{
            top: `${100 - mousePosition.y * 0.03}%`,
            right: `${100 - mousePosition.x * 0.03}%`,
            transform: 'translate(50%, 50%)'
          }}
        ></div>
        <div 
          className="absolute w-64 h-64 bg-gradient-to-r from-green-400 to-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-15 animate-pulse"
          style={{
            top: `${mousePosition.y * 0.01}%`,
            left: `${mousePosition.x * 0.01}%`,
            transform: 'translate(-30%, -30%)'
          }}
        ></div>

        {/* Floating particles */}
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-2 h-2 bg-white/20 rounded-full animate-pulse"
            style={{
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 3}s`,
              animationDuration: `${2 + Math.random() * 3}s`
            }}
          ></div>
        ))}
      </div>

      <div className="max-w-md w-full space-y-8 relative z-10">
        {/* Enhanced Header Section */}
        <div className="text-center">
          <div className="relative mx-auto h-20 w-20 mb-6">
            {/* Glowing effect */}
            <div className="absolute inset-0 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-2xl blur-lg opacity-50 animate-pulse"></div>
            <div className="relative h-20 w-20 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-2xl flex items-center justify-center shadow-2xl transform hover:scale-110 transition-all duration-300">
              <Store className="h-12 w-12 text-white" />
            </div>
            {/* Sparkle effects */}
            <div className="absolute -top-2 -right-2">
              <Sparkles className="w-6 h-6 text-yellow-400 animate-spin" style={{ animationDuration: '3s' }} />
            </div>
            <div className="absolute -bottom-1 -left-1">
              <Zap className="w-4 h-4 text-blue-400 animate-bounce" style={{ animationDelay: '1s' }} />
            </div>
          </div>

          <h2 className="text-5xl font-bold bg-gradient-to-r from-gray-900 via-blue-600 to-purple-600 bg-clip-text text-transparent mb-2">
            Welcome to Xenoxify
          </h2>
          <p className="text-lg text-gray-600 font-medium mb-2">
            Multi-tenant Shopify analytics platform
          </p>
          <div className="flex items-center justify-center space-x-2 text-sm text-gray-500">
            <Shield className="w-4 h-4" />
            <span>Secure • Fast • Reliable</span>
          </div>
        </div>

        {/* Enhanced Form Card with Glassmorphism */}
        <div className="relative">
          {/* Card glow effect */}
          <div className="absolute inset-0 bg-gradient-to-r from-blue-500/20 to-purple-500/20 rounded-3xl blur-xl"></div>

          <div className="relative bg-white/10 backdrop-blur-xl py-12 px-10 shadow-2xl rounded-3xl border border-white/20">
            {/* Animated background pattern */}
            <div className="absolute inset-0 rounded-3xl overflow-hidden">
              <div className="absolute inset-0 bg-gradient-to-br from-white/5 to-transparent"></div>
              <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(59,130,246,0.1),transparent_50%)]"></div>
            </div>

            <form className="space-y-8 relative z-10" onSubmit={handleSubmit}>
              {/* Email Field with Enhanced Validation */}
              <div className="space-y-3">
                <label htmlFor="email" className="block text-sm font-semibold text-gray-700 flex items-center">
                  <Mail className="w-4 h-4 mr-2" />
                  Email Address
                </label>
                <div className="relative group">
                  <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                    <Mail className={`h-5 w-5 transition-colors duration-200 ${
                      emailValid === true ? 'text-green-500' : 
                      emailValid === false ? 'text-red-500' : 
                      'text-gray-400 group-focus-within:text-blue-500'
                    }`} />
                  </div>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    autoComplete="email"
                    required
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl shadow-sm focus:outline-none focus:ring-4 transition-all duration-200 bg-white/50 focus:bg-white ${
                      emailValid === true ? 'border-green-300 focus:ring-green-100 focus:border-green-500' :
                      emailValid === false ? 'border-red-300 focus:ring-red-100 focus:border-red-500' :
                      'border-gray-200 focus:ring-blue-100 focus:border-blue-500'
                    }`}
                    placeholder="Enter your email"
                    value={email}
                    onChange={handleEmailChange}
                  />
                  <div className="absolute inset-y-0 right-0 pr-4 flex items-center">
                    {emailValid === true && <CheckCircle className="w-5 h-5 text-green-500" />}
                    {emailValid === false && <AlertCircle className="w-5 h-5 text-red-500" />}
                  </div>
                </div>
                {emailValid === false && (
                  <p className="text-xs text-red-500 flex items-center">
                    <AlertCircle className="w-3 h-3 mr-1" />
                    Please enter a valid email address
                  </p>
                )}
              </div>

              {/* Tenant ID Field with Enhanced Validation */}
              <div className="space-y-3">
                <label htmlFor="tenantId" className="block text-sm font-semibold text-gray-700 flex items-center">
                  <Key className="w-4 h-4 mr-2" />
                  Tenant ID
                </label>
                <div className="relative group">
                  <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                    <Key className={`h-5 w-5 transition-colors duration-200 ${
                      tenantValid === true ? 'text-green-500' : 
                      tenantValid === false ? 'text-red-500' : 
                      'text-gray-400 group-focus-within:text-blue-500'
                    }`} />
                  </div>
                  <input
                    id="tenantId"
                    name="tenantId"
                    type="text"
                    required
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl shadow-sm focus:outline-none focus:ring-4 transition-all duration-200 bg-white/50 focus:bg-white ${
                      tenantValid === true ? 'border-green-300 focus:ring-green-100 focus:border-green-500' :
                      tenantValid === false ? 'border-red-300 focus:ring-red-100 focus:border-red-500' :
                      'border-gray-200 focus:ring-blue-100 focus:border-blue-500'
                    }`}
                    placeholder="Enter tenant ID"
                    value={tenantId}
                    onChange={handleTenantChange}
                  />
                  <div className="absolute inset-y-0 right-0 pr-4 flex items-center">
                    {tenantValid === true && <CheckCircle className="w-5 h-5 text-green-500" />}
                    {tenantValid === false && <AlertCircle className="w-5 h-5 text-red-500" />}
                  </div>
                </div>
                <p className="text-xs text-gray-500 font-medium flex items-center">
                  <Shield className="w-3 h-3 mr-1" />
                  Get this from your Shopify app installation
                </p>
                {tenantValid === false && (
                  <p className="text-xs text-red-500 flex items-center">
                    <AlertCircle className="w-3 h-3 mr-1" />
                    Tenant ID must be at least 3 characters
                  </p>
                )}
              </div>

              {/* Enhanced Sign In Button */}
              <div className="pt-4">
                <button
                  type="submit"
                  disabled={loading || emailValid === false || tenantValid === false}
                  className="group relative w-full flex justify-center py-5 px-8 border border-transparent text-lg font-semibold rounded-xl text-white bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 hover:from-blue-700 hover:via-purple-700 hover:to-indigo-700 focus:outline-none focus:ring-4 focus:ring-purple-100 disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-[1.02] transition-all duration-300 shadow-2xl hover:shadow-purple-500/25"
                >
                  {/* Button glow effect */}
                  <div className="absolute inset-0 bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 rounded-xl blur opacity-75 group-hover:opacity-100 transition-opacity duration-300"></div>

                  {loading ? (
                    <div className="relative flex items-center space-x-3">
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                      <span>Signing In...</span>
                    </div>
                  ) : (
                    <span className="relative flex items-center space-x-3">
                      <span>Sign In</span>
                      <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-200" />
                    </span>
                  )}
                </button>
              </div>
            </form>

            {/* Enhanced Divider */}
            <div className="mt-10">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-300/50" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-6 bg-white/80 text-gray-500 font-medium">Or continue with</span>
                </div>
              </div>

              {/* Social Login Options */}
              <div className="mt-8 grid grid-cols-2 gap-4">
                <button
                  type="button"
                  onClick={handleShopifyInstall}
                  className="group flex justify-center items-center py-4 px-6 border-2 border-gray-200 rounded-xl shadow-sm bg-white/50 hover:bg-white hover:border-gray-300 text-base font-semibold text-gray-700 focus:outline-none focus:ring-4 focus:ring-gray-100 transition-all duration-200 transform hover:scale-[1.02]"
                >
                  <svg className="w-6 h-6 mr-3 group-hover:scale-110 transition-transform duration-200" viewBox="0 0 24 24" fill="currentColor">
                    <path d="M15.337 23.979c-.35-.002-.699-.104-.995-.301L3.2 15.4c-.622-.408-.622-1.07 0-1.478L14.342 5.32c.296-.197.645-.299.995-.301.35.002.699.104.995.301L26.8 13.922c.622.408.622 1.07 0 1.478L16.332 23.678c-.296.197-.645.299-.995.301z"/>
                  </svg>
                  Shopify
                </button>

                <button
                  type="button"
                  className="group flex justify-center items-center py-4 px-6 border-2 border-gray-200 rounded-xl shadow-sm bg-white/50 hover:bg-white hover:border-gray-300 text-base font-semibold text-gray-700 focus:outline-none focus:ring-4 focus:ring-gray-100 transition-all duration-200 transform hover:scale-[1.02]"
                >
                  <Github className="w-6 h-6 mr-3 group-hover:scale-110 transition-transform duration-200" />
                  GitHub
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Footer */}
        <div className="text-center space-y-4">
          <div className="flex items-center justify-center space-x-6 text-sm">
            <a href="#" className="text-blue-600 hover:text-blue-700 font-semibold transition-colors duration-200 flex items-center">
              <Shield className="w-4 h-4 mr-1" />
              Security
            </a>
            <a href="#" className="text-blue-600 hover:text-blue-700 font-semibold transition-colors duration-200 flex items-center">
              <Chrome className="w-4 h-4 mr-1" />
              Documentation
            </a>
            <a href="#" className="text-blue-600 hover:text-blue-700 font-semibold transition-colors duration-200 flex items-center">
              <Mail className="w-4 h-4 mr-1" />
              Support
            </a>
          </div>

          <p className="text-xs text-gray-500">
            By signing in, you agree to our Terms of Service and Privacy Policy
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
