import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Filter, Grid, List, Package, DollarSign, Hash, BarChart3, Eye, MoreVertical, ChevronLeft, ChevronRight, Loader2 } from 'lucide-react';

// API configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const Products = () => {
  // State with proper initial values
  const [products, setProducts] = useState([]);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
  const [sortBy, setSortBy] = useState('name'); // 'name', 'price', 'stock', 'status'
  const [sortOrder, setSortOrder] = useState('asc'); // 'asc' or 'desc'

  // Fetch products data
  const fetchProducts = async (page = 1, limit = 10, search = '') => {
    try {
      setLoading(true);
      setError(null);

      // Make API call with pagination and search
      const response = await axios.get(`${API_BASE_URL}/api/shopify/products`, {
        params: {
          page,
          limit,
          search
        }
      });

      // Handle response data with fallbacks
      const responseData = response.data || {};
      setProducts(responseData.data || []); // Correctly access the nested 'data' array
      setPagination({
        page: responseData.page || 1,
        limit: responseData.limit || 10,
        total: responseData.count || 0, // Use the 'count' property from the API response
        totalPages: responseData.totalPages || 1
      });

      console.log('Products loaded successfully');
    } catch (error) {
      console.error('Products fetch error:', error);
      setError('Failed to load products. Please check if the server is running.');
      
      // Set empty state on error
      setProducts([]);
      setPagination({
        page: 1,
        limit: 10,
        total: 0,
        totalPages: 0
      });
    } finally {
      setLoading(false);
    }
  };

  // Handle pagination
  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= pagination.totalPages) {
      fetchProducts(newPage, pagination.limit, searchTerm);
    }
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    fetchProducts(1, pagination.limit, searchTerm);
  };

  // Initial load
  useEffect(() => {
    fetchProducts();
  }, []);

  // Loading state
  if (loading) {
    return (
      <div className="products-loading">
        <h2>Loading Products...</h2>
        <p>Fetching your products...</p>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="products-error">
        <h2>Products Error</h2>
        <p>{error}</p>
        <button onClick={() => fetchProducts()}>Retry</button>
      </div>
    );
  }

  return (
    <div className="products">
      <div className="products-header">
        <h1>Products</h1>
        
        {/* Search */}
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

      {/* Products List */}
      <div className="products-list">
        {products && products.length > 0 ? (
          <div>
            <div className="products-grid">
              {products.map((product, index) => (
                <div key={product.id || index} className="product-card">
                  <h3>{product.name || product.title || 'Unnamed Product'}</h3>
                  <p>Price: ${product.price || '0.00'}</p>
                  <p>SKU: {product.sku || 'N/A'}</p>
                  <p>Stock: {product.stock || product.inventory || 0}</p>
                  <p>Status: {product.status || 'Unknown'}</p>
                </div>
              ))}
            </div>
            
            {/* Pagination */}
            <div className="pagination">
              <button 
                onClick={() => handlePageChange(pagination.page - 1)}
                disabled={pagination.page <= 1}
              >
                Previous
              </button>
              
              <span>
                Page {pagination.page} of {pagination.totalPages} 
                ({pagination.total} total products)
              </span>
              
              <button 
                onClick={() => handlePageChange(pagination.page + 1)}
                disabled={pagination.page >= pagination.totalPages}
              >
                Next
              </button>
            </div>
          </div>
        ) : (
          <div className="no-products">
            <p>No products found</p>
            <button onClick={() => fetchProducts()}>Refresh</button>
          </div>
        )}
      </div>

      {/* Debug Info (remove in production) */}
      <div className="debug-info" style={{ marginTop: '20px', padding: '10px', background: '#f5f5f5' }}>
        <h3>Debug Info:</h3>
        <p>API Base URL: {API_BASE_URL}</p>
        <p>Products Count: {products?.length || 0}</p>
        <p>Current Page: {pagination.page}</p>
        <p>Total Pages: {pagination.totalPages}</p>
        <p>Search Term: "{searchTerm}"</p>
      </div>
    </div>
  );
};

export default Products;
