import React, { useState, useEffect } from 'react';
import axios from 'axios';

// API configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const Orders = () => {
  // State with proper initial values - THIS PREVENTS THE ERROR
  const [orders, setOrders] = useState([]);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    totalPages: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  // Fetch orders data
  const fetchOrders = async (page = 1, limit = 20, search = '', status = 'all') => {
    try {
      setLoading(true);
      setError(null);

      console.log(`Fetching orders from: ${API_BASE_URL}/api/orders`);

      // Make API call with correct base URL
      const response = await axios.get(`${API_BASE_URL}/api/orders`, {
        params: {
          page,
          limit,
          search,
          status: status !== 'all' ? status : undefined
        }
      });

      console.log('Orders API response:', response.data);

      // Handle response data with fallbacks
      const data = response.data || {};
      setOrders(data.orders || []);
      setPagination({
        page: data.page || 1,
        limit: data.limit || 20,
        total: data.total || 0,
        totalPages: data.totalPages || 1
      });

      console.log('Orders loaded successfully');
    } catch (error) {
      console.error('Orders fetch error:', error);
      setError('Failed to load orders. Please check if the server is running.');
      
      // Set empty state on error
      setOrders([]);
      setPagination({
        page: 1,
        limit: 20,
        total: 0,
        totalPages: 0
      });
    } finally {
      setLoading(false);
    }
  };

  // Handle pagination
  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= pagination.totalPages) {
      fetchOrders(newPage, pagination.limit, searchTerm, statusFilter);
    }
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    fetchOrders(1, pagination.limit, searchTerm, statusFilter);
  };

  // Handle status filter change
  const handleStatusChange = (newStatus) => {
    setStatusFilter(newStatus);
    fetchOrders(1, pagination.limit, searchTerm, newStatus);
  };

  // Initial load
  useEffect(() => {
    fetchOrders();
  }, []);

  // Loading state
  if (loading) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h2>Loading Orders...</h2>
        <p>Fetching order data...</p>
        <div>Please wait...</div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div style={{ padding: '20px', textAlign: 'center', color: 'red' }}>
        <h2>Orders Error</h2>
        <p>{error}</p>
        <button 
          onClick={() => fetchOrders()}
          style={{ padding: '10px 20px', marginTop: '10px' }}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ marginBottom: '20px' }}>
        <h1>Orders Management</h1>
        
        <div style={{ display: 'flex', gap: '15px', marginBottom: '20px', flexWrap: 'wrap' }}>
          {/* Search */}
          <form onSubmit={handleSearch} style={{ display: 'flex', gap: '10px' }}>
            <input
              type="text"
              placeholder="Search orders..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
            />
            <button 
              type="submit"
              style={{ padding: '8px 15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}
            >
              Search
            </button>
          </form>

          {/* Status Filter */}
          <select 
            value={statusFilter} 
            onChange={(e) => handleStatusChange(e.target.value)}
            style={{ padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}
          >
            <option value="all">All Orders</option>
            <option value="pending">Pending</option>
            <option value="processing">Processing</option>
            <option value="shipped">Shipped</option>
            <option value="delivered">Delivered</option>
            <option value="cancelled">Cancelled</option>
          </select>
        </div>
      </div>

      {/* Orders List */}
      <div>
        {orders && orders.length > 0 ? (
          <div>
            {/* Orders Table */}
            <div style={{ overflowX: 'auto' }}>
              <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ddd' }}>
                <thead>
                  <tr style={{ backgroundColor: '#f8f9fa' }}>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Order ID</th>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Customer</th>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Date</th>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Items</th>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Total</th>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Status</th>
                    <th style={{ padding: '12px', border: '1px solid #ddd', textAlign: 'left' }}>Payment</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.map((order, index) => (
                    <tr key={order.id || index}>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        #{order.id || order.orderNumber}
                      </td>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        {order.customerName || order.customer || 'N/A'}
                      </td>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        {order.date || order.createdAt || 'N/A'}
                      </td>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        {order.itemCount || order.items || 0}
                      </td>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        ${order.total || order.amount || '0.00'}
                      </td>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        <span 
                          style={{ 
                            padding: '4px 8px', 
                            borderRadius: '4px', 
                            fontSize: '12px',
                            backgroundColor: getStatusColor(order.status),
                            color: 'white'
                          }}
                        >
                          {order.status || 'Unknown'}
                        </span>
                      </td>
                      <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                        {order.paymentStatus || 'N/A'}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {/* Pagination */}
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '15px', marginTop: '20px' }}>
              <button 
                onClick={() => handlePageChange(pagination.page - 1)}
                disabled={pagination.page <= 1}
                style={{ 
                  padding: '8px 15px', 
                  backgroundColor: pagination.page <= 1 ? '#ccc' : '#007bff',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '4px',
                  cursor: pagination.page <= 1 ? 'not-allowed' : 'pointer'
                }}
              >
                Previous
              </button>
              
              <span style={{ fontSize: '14px' }}>
                Page {pagination.page} of {pagination.totalPages} 
                ({pagination.total} total orders)
              </span>
              
              <button 
                onClick={() => handlePageChange(pagination.page + 1)}
                disabled={pagination.page >= pagination.totalPages}
                style={{ 
                  padding: '8px 15px', 
                  backgroundColor: pagination.page >= pagination.totalPages ? '#ccc' : '#007bff',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '4px',
                  cursor: pagination.page >= pagination.totalPages ? 'not-allowed' : 'pointer'
                }}
              >
                Next
              </button>
            </div>
          </div>
        ) : (
          <div style={{ textAlign: 'center', padding: '40px' }}>
            <p>No orders found</p>
            <button 
              onClick={() => fetchOrders()}
              style={{ padding: '10px 20px', marginTop: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}
            >
              Refresh
            </button>
          </div>
        )}
      </div>

      {/* Debug Info (remove in production) */}
      <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f5f5f5', borderRadius: '4px', fontSize: '12px' }}>
        <h3>Debug Info:</h3>
        <p><strong>API Base URL:</strong> {API_BASE_URL}</p>
        <p><strong>Orders Count:</strong> {orders?.length || 0}</p>
        <p><strong>Current Page:</strong> {pagination.page}</p>
        <p><strong>Total Pages:</strong> {pagination.totalPages}</p>
        <p><strong>Status Filter:</strong> {statusFilter}</p>
        <p><strong>Search Term:</strong> "{searchTerm}"</p>
      </div>
    </div>
  );
};

// Helper function for status colors
const getStatusColor = (status) => {
  switch (status?.toLowerCase()) {
    case 'pending': return '#ffc107';
    case 'processing': return '#17a2b8';
    case 'shipped': return '#007bff';
    case 'delivered': return '#28a745';
    case 'cancelled': return '#dc3545';
    default: return '#6c757d';
  }
};

export default Orders;
