import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  TrendingUp, 
  TrendingDown, 
  Users, 
  ShoppingBag, 
  DollarSign, 
  Package, 
  Eye, 
  MoreVertical,
  Calendar,
  ArrowUpRight,
  ArrowDownRight,
  Activity,
  Target,
  Zap,
  Star
} from 'lucide-react';

// API configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const Dashboard = () => {
  // State with proper initial values
  const [summaryData, setSummaryData] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    totalCustomers: 0,
    averageOrderValue: 0,
    growthRate: 0,
    conversionRate: 0
  });
  // NOTE: Removed the redundant `topCustomers` state. We will manage it inside chartData.
  const [orders, setOrders] = useState([]);
  const [chartData, setChartData] = useState({
    salesOverTime: [],
    orderStatusDistribution: [],
    paymentStatus: [],
    topCustomersByRevenue: [] // This will hold our real customer data
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState('30d');

  // Generate realistic mock data
  const generateMockData = () => {
    // This function can now be simplified as it doesn't need to handle top customers.
    const salesOverTime = [];
    const days = timeRange === '7d' ? 7 : timeRange === '30d' ? 30 : 90;
    for (let i = days - 1; i >= 0; i--) {
      const date = new Date();
      date.setDate(date.getDate() - i);
      salesOverTime.push({
        date: date.toISOString().split('T')[0],
        sales: Math.floor(Math.random() * 300) + 150
      });
    }

    const orderStatusDistribution = [
      { status: 'delivered', count: 45, color: '#10B981' },
      { status: 'shipped', count: 32, color: '#3B82F6' },
      { status: 'pending', count: 28, color: '#F59E0B' },
      { status: 'processing', count: 18, color: '#8B5CF6' },
      { status: 'cancelled', count: 12, color: '#EF4444' },
      { status: 'refunded', count: 8, color: '#F97316' }
    ];

    const paymentStatus = [
      { status: 'paid', count: 120, color: '#10B981' },
      { status: 'pending', count: 25, color: '#F59E0B' },
      { status: 'failed', count: 8, color: '#EF4444' },
      { status: 'refunded', count: 5, color: '#6B7280' }
    ];

    const recentOrders = [
      { id: 'ORD-001', customer: 'John Doe', amount: 250, status: 'delivered', date: '2025-09-10' },
      { id: 'ORD-002', customer: 'Jane Smith', amount: 180, status: 'shipped', date: '2025-09-09' },
      { id: 'ORD-003', customer: 'Mike Johnson', amount: 320, status: 'pending', date: '2025-09-08' },
      { id: 'ORD-004', customer: 'Sarah Wilson', amount: 420, status: 'delivered', date: '2025-09-07' },
      { id: 'ORD-005', customer: 'Tom Brown', amount: 150, status: 'processing', date: '2025-09-06' }
    ];

    return {
      summaryData: {
        totalOrders: 150,
        totalRevenue: 45000,
        averageOrderValue: 300,
        growthRate: 12.5,
        conversionRate: 3.2
      },
      orders: recentOrders,
      chartData: {
        salesOverTime,
        orderStatusDistribution,
        paymentStatus,
      }
    };
  };

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch real customer data from API
      const customersResponse = await axios.get(`${API_BASE_URL}/api/shopify/customers`);
      const realCustomers = customersResponse.data.data;

      // ---- CHANGE #1: Transform API data to match the component's expected structure ----
      const transformedCustomers = realCustomers.map(customer => ({
        name: `${customer.first_name} ${customer.last_name}`,
        orders: customer.orders_count,
        revenue: parseFloat(customer.total_spent), // Convert string to number
        avatar: `${customer.first_name[0]}${customer.last_name[0]}`.toUpperCase()
      }));

      const mockData = generateMockData();

