import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Filter, Users, Mail, Phone, MapPin, Calendar, DollarSign, ShoppingBag, Eye, MoreVertical, ChevronLeft, ChevronRight, User, Star, TrendingUp, Clock } from 'lucide-react';

// API configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const Customers = () => {
  // State with proper initial values
  const [customers, setCustomers] = useState([]);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    totalPages: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('cards'); // 'cards' or 'table'
  const [sortBy, setSortBy] = useState('name'); // 'name', 'total_spent', 'orders_count', 'join_date'
  const [sortOrder, setSortOrder] = useState('asc'); // 'asc' or 'desc'

  // Fetch customers data
  const fetchCustomers = async (page = 1, limit = 20, search = '') => {
    try {
      setLoading(true);
      setError(null);

      // Make API call with correct base URL
      const response = await axios.get(`${API_BASE_URL}/api/shopify/customers`, {
        params: {
          page,
          limit,
          search
        }
      });

      // Handle response data with fallbacks
      const data = response.data || {};
      setCustomers(data.data || []);
      setPagination({
        page: data.page || 1,
        limit: data.limit || 20,
        total: data.total || 0,
        totalPages: data.totalPages || 1
      });

      console.log('Customers loaded successfully');
    } catch (error) {
      console.error('Customers fetch error:', error);
      setError('Failed to load customers. Please check if the server is running.');
      
      // Set empty state on error
      setCustomers([]);
      setPagination({
        page: 1,
        limit: 20,
        total: 0,
        totalPages: 0
      });
    } finally {
      setLoading(false);
    }
  };

  // Handle pagination
  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= pagination.totalPages) {
      fetchCustomers(newPage, pagination.limit, searchTerm);
    }
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    fetchCustomers(1, pagination.limit, searchTerm);
  };

  // Initial load
  useEffect(() => {
    fetchCustomers();
  }, []);

  // Helper function to get customer initials
  const getCustomerInitials = (customer) => {
    const name = customer.name || `${customer.first_name || ''} ${customer.last_name || ''}`.trim();
    if (!name || name === 'N/A') return 'U';
    const words = name.split(' ');
    if (words.length >= 2) {
      return (words[0][0] + words[1][0]).toUpperCase();
    }
    return name.substring(0, 2).toUpperCase();
  };

  // Helper function to format currency
  const formatCurrency = (amount) => {
    const numAmount = parseFloat(amount) || 0;
    return numAmount.toLocaleString('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    });
  };

  // Helper function to format date
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch {
      return 'N/A';
    }
  };

  // Helper function to get customer status
  const getCustomerStatus = (customer) => {
    const totalSpent = parseFloat(customer.total_spent) || 0;
    const ordersCount = customer.orders_count || 0;
    
    if (totalSpent > 10000) return { label: 'VIP', color: 'bg-purple-100 text-purple-800 border-purple-200' };
    if (totalSpent > 5000) return { label: 'Premium', color: 'bg-blue-100 text-blue-800 border-blue-200' };
    if (ordersCount > 5) return { label: 'Regular', color: 'bg-green-100 text-green-800 border-green-200' };
    if (ordersCount > 0) return { label: 'New', color: 'bg-yellow-100 text-yellow-800 border-yellow-200' };
    return { label: 'Prospect', color: 'bg-gray-100 text-gray-800 border-gray-200' };
  };

  // Helper function to get customer avatar color
  const getAvatarColor = (customer) => {
    const name = customer.name || `${customer.first_name || ''} ${customer.last_name || ''}`.trim();
    if (!name || name === 'N/A') return 'from-blue-500 to-blue-600';
    
    const hash = name.split('').reduce((a, b) => {
      a = ((a << 5) - a) + b.charCodeAt(0);
      return a & a;
    }, 0);
    
    const colors = [
      'from-red-500 to-red-600',
      'from-blue-500 to-blue-600',
      'from-green-500 to-green-600',
      'from-yellow-500 to-yellow-600',
      'from-purple-500 to-purple-600',
      'from-pink-500 to-pink-600',
      'from-indigo-500 to-indigo-600',
      'from-orange-500 to-orange-600'
    ];
    
    return colors[Math.abs(hash) % colors.length];
  };

  // Loading state
  if (loading) {
    return (
      <div className="customers-loading">
        <h2>Loading Customers...</h2>
        <p>Fetching customer data...</p>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="customers-error">
        <h2>Customers Error</h2>
        <p>{error}</p>
        <button onClick={() => fetchCustomers()}>Retry</button>
      </div>
    );
  }

  return (
    <div className="customers">
      <div className="customers-header">
        <h1>Customers</h1>
        
        {/* Search */}
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search customers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

      {/* Customers List */}
      <div className="customers-list">
        {customers && customers.length > 0 ? (
          <div>
            {/* Customers Table */}
            <div className="customers-table">
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Orders</th>
                    <th>Total Spent</th>
                    <th>Phone Number</th>
                    <th>Address</th>
                    <th>Join Date</th>
                  </tr>
                </thead>
                <tbody>
                  {customers.map((customer, index) => (
                    <tr key={customer.id || index}>
                      <td>{customer.id}</td>
                      <td>{customer.name || customer.first_name + ' ' + customer.last_name || 'N/A'}</td>
                      <td>{customer.email || 'N/A'}</td>
                      <td>{customer.orders_count || 0}</td>
                      <td>{customer.total_spent || '0.00'}</td>
                      <td>{customer.phone || '+91XXXXX-XXXXX'}</td>
                      <td>{customer.default_address.address1}, {customer.default_address.city} ({customer.default_address.zip})</td>
                      <td>{customer.joinDate || customer.created_at || 'N/A'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {/* Pagination */}
            <div className="pagination">
              <button 
                onClick={() => handlePageChange(pagination.page - 1)}
                disabled={pagination.page <= 1}
              >
                Previous
              </button>
              
              <span>
                Page {pagination.page} of {pagination.totalPages} 
                ({pagination.total} total customers)
              </span>
              
              <button 
                onClick={() => handlePageChange(pagination.page + 1)}
                disabled={pagination.page >= pagination.totalPages}
              >
                Next
              </button>
            </div>
          </div>
        ) : (
          <div className="no-customers">
            <p>No customers found</p>
            <button onClick={() => fetchCustomers()}>Refresh</button>
          </div>
        )}
      </div>

      {/* Debug Info (remove in production) */}
      <div className="debug-info" style={{ marginTop: '20px', padding: '10px', background: '#f5f5f5' }}>
        <h3>Debug Info:</h3>
        <p>API Base URL: {API_BASE_URL}</p>
        <p>Customers Count: {customers?.length || 0}</p>
        <p>Current Page: {pagination.page}</p>
        <p>Total Pages: {pagination.totalPages}</p>
        <p>Search Term: "{searchTerm}"</p>
      </div>
    </div>
  );
};

export default Customers;
