import React, { useState, useEffect } from 'react';
import axios from 'axios';

// API configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const Customers = () => {
  // State with proper initial values
  const [customers, setCustomers] = useState([]);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    totalPages: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch customers data
  const fetchCustomers = async (page = 1, limit = 20, search = '') => {
    try {
      setLoading(true);
      setError(null);

      // Make API call with correct base URL
      const response = await axios.get(`${API_BASE_URL}/api/shopify/customers`, {
        params: {
          page,
          limit,
          search
        }
      });

      // Handle response data with fallbacks
      const data = response.data || {};
      setCustomers(data.dat || []);
      setPagination({
        page: data.page || 1,
        limit: data.limit || 20,
        total: data.total || 0,
        totalPages: data.totalPages || 1
      });

      console.log('Customers loaded successfully');
    } catch (error) {
      console.error('Customers fetch error:', error);
      setError('Failed to load customers. Please check if the server is running.');
      
      // Set empty state on error
      setCustomers([]);
      setPagination({
        page: 1,
        limit: 20,
        total: 0,
        totalPages: 0
      });
    } finally {
      setLoading(false);
    }
  };

  // Handle pagination
  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= pagination.totalPages) {
      fetchCustomers(newPage, pagination.limit, searchTerm);
    }
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    fetchCustomers(1, pagination.limit, searchTerm);
  };

  // Initial load
  useEffect(() => {
    fetchCustomers();
  }, []);

  // Loading state
  if (loading) {
    return (
      <div className="customers-loading">
        <h2>Loading Customers...</h2>
        <p>Fetching customer data...</p>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="customers-error">
        <h2>Customers Error</h2>
        <p>{error}</p>
        <button onClick={() => fetchCustomers()}>Retry</button>
      </div>
    );
  }

  return (
    <div className="customers">
      <div className="customers-header">
        <h1>Customers</h1>
        
        {/* Search */}
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search customers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

      {/* Customers List */}
      <div className="customers-list">
        {customers && customers.length > 0 ? (
          <div>
            {/* Customers Table */}
            <div className="customers-table">
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Orders</th>
                    <th>Total Spent</th>
                    <th>Status</th>
                    <th>Join Date</th>
                  </tr>
                </thead>
                <tbody>
                  {customers.map((customer, index) => (
                    <tr key={customer.id || index}>
                      <td>{customer.id}</td>
                      <td>{customer.name || customer.firstName + ' ' + customer.lastName || 'N/A'}</td>
                      <td>{customer.email || 'N/A'}</td>
                      <td>{customer.totalOrders || 0}</td>
                      <td>${customer.totalSpent || '0.00'}</td>
                      <td>{customer.status || 'Active'}</td>
                      <td>{customer.joinDate || customer.createdAt || 'N/A'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {/* Pagination */}
            <div className="pagination">
              <button 
                onClick={() => handlePageChange(pagination.page - 1)}
                disabled={pagination.page <= 1}
              >
                Previous
              </button>
              
              <span>
                Page {pagination.page} of {pagination.totalPages} 
                ({pagination.total} total customers)
              </span>
              
              <button 
                onClick={() => handlePageChange(pagination.page + 1)}
                disabled={pagination.page >= pagination.totalPages}
              >
                Next
              </button>
            </div>
          </div>
        ) : (
          <div className="no-customers">
            <p>No customers found</p>
            <button onClick={() => fetchCustomers()}>Refresh</button>
          </div>
        )}
      </div>

      {/* Debug Info (remove in production) */}
      <div className="debug-info" style={{ marginTop: '20px', padding: '10px', background: '#f5f5f5' }}>
        <h3>Debug Info:</h3>
        <p>API Base URL: {API_BASE_URL}</p>
        <p>Customers Count: {customers?.length || 0}</p>
        <p>Current Page: {pagination.page}</p>
        <p>Total Pages: {pagination.totalPages}</p>
        <p>Search Term: "{searchTerm}"</p>
      </div>
    </div>
  );
};

export default Customers;
