import React, { useState, useEffect } from 'react';
import axios from 'axios';
import OrderCharts from './Charts/OrderCharts'; // Add this import

// API configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const Dashboard = () => {
  // State with proper initial values
  const [summaryData, setSummaryData] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    totalCustomers: 0,
    averageOrderValue: 0
  });
  const [topCustomers, setTopCustomers] = useState([]); // Initialize as empty array
  const [orders, setOrders] = useState([]); // Initialize as empty array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Make API calls with proper base URL
      const [summaryRes, customersRes, ordersRes] = await Promise.all([
        axios.get(`${API_BASE_URL}/api/insights/summary?period=30`),
        axios.get(`${API_BASE_URL}/api/insights/top-customers?limit=5`),
        axios.get(`${API_BASE_URL}/api/insights/orders?from=2025-08-14&to=2025-09-13`)
      ]);

      // Set data with fallbacks and proper mapping
      setSummaryData(summaryRes.data || {});

      // Map top customers data to match component expectations
      const mappedTopCustomers = (customersRes.data || []).map(customer => ({
        id: customer.id,
        name: `${customer.first_name || ''} ${customer.last_name || ''}`.trim() || customer.email,
        orders: customer.orders_count || 0,
        revenue: customer.total_spent || 0,
        email: customer.email
      }));
      setTopCustomers(mappedTopCustomers);

      // Map orders data to match component expectations
      const mappedOrders = (ordersRes.data?.orders || []).map(order => ({
        id: order.name || order.id,
        date: new Date(order.created_at).toLocaleDateString(),
        customer: order.customer ? `${order.customer.first_name || ''} ${order.customer.last_name || ''}`.trim() || order.customer.email : 'Guest',
        amount: parseFloat(order.total_price || 0).toFixed(2),
        status: order.fulfillment_status || order.financial_status || 'N/A'
      }));
      setOrders(mappedOrders);

      console.log('Dashboard data loaded successfully');
    } catch (error) {
      console.error('Dashboard data fetch error:', error);
      setError('Failed to load dashboard data. Please check if the server is running.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  // Loading state
  if (loading) {
    return (
      <div className="dashboard-loading">
        <h2>Loading Dashboard...</h2>
        <p>Fetching your data...</p>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="dashboard-error">
        <h2>Dashboard Error</h2>
        <p>{error}</p>
        <button onClick={fetchDashboardData}>Retry</button>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {/* Add the charts component here */}
      <OrderCharts 
        orders={orders} 
        topCustomers={topCustomers} 
        summaryData={summaryData} 
      />

      {/* Summary Cards */}
      <div className="summary-cards">
        <div className="card">
          <h3>Total Orders</h3>
          <p>{summaryData.totalOrders || 0}</p>
        </div>
        <div className="card">
          <h3>Total Revenue</h3>
          <p>${summaryData.totalRevenue || 0}</p>
        </div>
        <div className="card">
          <h3>Total Customers</h3>
          <p>{summaryData.totalCustomers || 0}</p>
        </div>
        <div className="card">
          <h3>Average Order Value</h3>
          <p>${summaryData.averageOrderValue || 0}</p>
        </div>
      </div>

      {/* Top Customers */}
      <div className="top-customers">
        <h2>Top Customers</h2>
        {topCustomers && topCustomers.length > 0 ? (
          <ul>
            {topCustomers.map((customer, index) => (
              <li key={customer.id || index}>
                <span>{customer.name}</span>
                <span>{customer.orders} orders</span>
                <span>${customer.revenue}</span>
              </li>
            ))}
          </ul>
        ) : (
          <p>No customer data available</p>
        )}
      </div>

      {/* Recent Orders */}
      <div className="recent-orders">
        <h2>Recent Orders</h2>
        {orders && orders.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Date</th>
                <th>Customer</th>
                <th>Amount</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => (
                <tr key={order.id || index}>
                  <td>{order.id}</td>
                  <td>{order.date}</td>
                  <td>{order.customer}</td>
                  <td>${order.amount}</td>
                  <td>{order.status || 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No orders available</p>
        )}
      </div>

      {/* Debug Info (remove in production) */}
      <div className="debug-info" style={{ marginTop: '20px', padding: '10px', background: '#f5f5f5' }}>
        <h3>Debug Info:</h3>
        <p>API Base URL: {API_BASE_URL}</p>
        <p>Summary Data: {JSON.stringify(summaryData)}</p>
        <p>Top Customers Count: {topCustomers?.length || 0}</p>
        <p>Orders Count: {orders?.length || 0}</p>
      </div>
    </div>
  );
};

export default Dashboard;
